} else {
	$sql = "SELECT p.*, pd.*, p2c1.main_category, m.name AS manufacturer, IF(p2c1.main_category = '1', (SELECT GROUP_CONCAT(cd1.name ORDER BY level SEPARATOR '&nbsp;&nbsp;&gt;&nbsp;&nbsp;') FROM " . DB_PREFIX . "category_path cp LEFT JOIN " . DB_PREFIX . "category_description cd1 ON (cp.path_id = cd1.category_id) WHERE cp.category_id = p2c1.category_id AND cd1.language_id = '" . (int)$this->config->get('config_language_id') . "' GROUP BY cp.category_id), '') AS category FROM " . DB_PREFIX . "product p LEFT JOIN " . DB_PREFIX . "product_description pd ON (p.product_id = pd.product_id) LEFT JOIN " . DB_PREFIX . "manufacturer m ON (p.manufacturer_id = m.manufacturer_id) LEFT JOIN " . DB_PREFIX . "product_to_category p2c1 ON (p.product_id = p2c1.product_id) LEFT JOIN " . DB_PREFIX . "category_description cd ON (p2c1.category_id = cd.category_id)";

	if (isset($data['filter_category_id']) && $data['filter_category_id'] !== '') {
		$sql .= " LEFT JOIN " . DB_PREFIX . "product_to_category p2c2 ON (p.product_id = p2c2.product_id) ";
	}

	$sql .= " WHERE pd.language_id = '" . (int)$this->config->get('config_language_id') . "' AND (cd.language_id = '" . (int)$this->config->get('config_language_id') . "' OR cd.language_id IS NULL) AND (p2c1.main_category = (SELECT MAX(p2c2.main_category) FROM " . DB_PREFIX . "product_to_category p2c2 WHERE p2c2.product_id = p.product_id) OR p2c1.main_category IS NULL)";

